import React from 'react'

import { BillContext } from '../Context/Bill'
import { useContext } from 'react'
import { useNavigate } from 'react-router-dom'
import { UserContext } from '../Context/UserContext'
import DisplayMap from '../Map/DisplayMap'
import { useState } from 'react'

function HotelsChild(props) {
let [bill,setbill]= useContext(BillContext)
let [context,setContext] = useContext(UserContext)
console.log(props.mode)

let navigate = useNavigate()
function confirmHotel(){
    
 setbill(bill + props.data.pricePerNight)
 setContext({...context,"hotelName":props.data.name})
 navigate('/Places')
}

const [showMap, setShowMap] = useState(false);
console.log(props.driver)

const handleTrackVehicle = () => {
setShowMap(true);
};

const handleCloseMap = () => {
setShowMap(false);
};


return (
<div>
    <div className='flex bg-white rounded-lg overflow-hidden  m-10 h-72 '>
        <div className=''>
            <img src='' alt='' className='h-full w-250px] '></img>
        </div>
        <div className='w-72'>
            <div className='flex'>
    
                <h1 className='m-2'>{props.data.name}</h1>
            </div>
            <div className='flex' >
                <h1 className='text-green-600 m-2'>Price/Night :</h1>
                <h1 className='m-2'>{props.data.pricePerNight}</h1>
            </div>
            
           <div className='flex'>
              
                <h1 className='m-2'>{props.data.description}</h1>
            </div> 
            
            
        
        <div className=' p-10'>
            <button className='bg-green-500 m-auto p-4 rounded-full' onClick={handleTrackVehicle}>Locate on map</button>
        </div>
        </div>
        
        
    </div>
    {showMap && (
    <div className="modal">
      <div className="modal-content">
        <DisplayMap handleCloseMap={props.handleCloseMap} center ={[props.data.latitude,props.data.longitude]} />
        <button className='bg-green-500 m-3 p-4 rounded-full w-20 text-white' onClick={handleCloseMap}>Close</button>
      </div>
    </div>
  )}
</div>
)

