import React from 'react'
import Booking from '../Booking/Booking';

function Tcc(props) {
    const handleBooking = (mode) => {
        // Handle booking based on the mode
        if(mode === 'Railways'){
            return <Booking data='train'/>
        }
        if(mode === 'Cabs'){
            return <Booking data='cabs'/>
        }
        if(mode === 'Airways'){
            return <Booking data='flight'/>
        }
        if(mode === 'MiniBus'){
            return <Booking data='minibus'/>
        }
        if(mode === 'Bus'){
            return <Booking data='bus'/>
        }

      };
  return (
    <div>
      <div key={props.index} className="w-[300px] rounded-lg overflow-hidden shadow-xl m-2">
                <div className='overflow-hidden object-cover' >
                  <img className="w-full h-40 transform origin-center transition-transform duration-400 object-cover hover:scale-110" src={props.data.url} alt="Your Image" />
                </div>
                <div className="px-6 py-4">
                  <div className="font-bold text-xl mb-2">{props.data.mode}</div>
                  <p className="text-gray-700 truncate">(Average Budget: {props.data.averageBudget})</p>
                  <button onClick={() => handleBooking(props.data.mode)} className='rounded-full bg-sky-600 p-3 text-white my-2'>View Options</button>
                </div>
              </div>
    </div>
  )
}

export default Tcc
